public class Constructor {    String name;    int ph;    boolean d;    Constructor(String n, int p, boolean d) {        this.name = n;        this.ph = p;        this.d = d;    }    void display() {        System.out.println(name);        System.out.println(ph);        System.out.println(d);    }}class print {    public static void main(String[] args) {        Constructor c = new Constructor("Dheeraj", 73107, false);        c.display();    }}