import java.util.ArrayList;import java.util.List;public class A___MergeArray {    public static void main(String[] args) {        int arr1[] = {1, 5, 7, 19};        int arr2[] = {2, 3, 4, 6, 8, 9, 10,17};        List <Integer> l = new ArrayList <> ();        int i = 0, j = 0;        while (i < arr1.length && j < arr2.length) {            if (arr1[i] < arr2[j]) {                l.add(arr1[i]);                i++;            } else {                l.add(arr2[j]);                j++;            }        }        while(i<arr1.length)        {            l.add(arr1[i++]);        }        while(j<arr2.length)        {            l.add(arr2[j++]);        }        System.out.println(l);    }}