public class BinaryString {    static int c = 0;    public static void main(String[] args) {        String s = "1??0?101";        String ans = "";        System.out.println(sequence(s, ans));    }    public static int sequence(String s, String ans) {        if (s.length() == 0) {            System.out.println(ans);            c++;            return c;        }        char ch = s.charAt(0);        if (ch == '?') {            sequence(s.substring(1), ans + "0");            sequence(s.substring(1), ans + "1");        } else {            sequence(s.substring(1), ans + ch);        }        return c;    }}