public class FirstIndex {    public static void main(String[] args) {        int[] a = {3, 1, 2, 2, 2};        int t = 2;        int c = first(a, t, 0);        System.out.println(c);    }    public static int first(int[] a, int t, int i) {        if (i == a.length) {            return -1;        }        if (a[i] == t) {            return i;        }        return first(a, t, i + 1);    }}