import java.util.Scanner;public class RatMaze {    public static void main(String args[]) {        Scanner sc = new Scanner(System.in);        int m = 5;        int n = 4;        char maze[][] = new char[m][n];        for (int i = 0; i < m; i++) {            String s = sc.next();            for (int j = 0; j < n; j++) {                maze[i][j] = s.charAt(j);            }        }        int ans[][] = new int[m][n];        rat_maze(m, n, 0, 0, maze, ans);        if (x == false) {            System.out.print("NO PATH FOUND");        }    }    static boolean x = false;    public static void rat_maze(int m, int n, int r, int c, char[][] maze, int ans[][]) {        if (c < 0 || c >= n || r < 0 || r >= m || maze[r][c] == 'X') {            return;        }        if (m - 1 == r && n - 1 == c) {            ans[r][c] = 1;            x = true;            for (int i = 0; i < m; i++) {                for (int j = 0; j < n; j++) {                    System.out.print(ans[i][j] + " ");                }                System.out.println();            }            System.out.println();            return;        }        maze[r][c] = 'X';        ans[r][c] = 1;        rat_maze(m, n, r + 1, c, maze, ans);        rat_maze(m, n, r, c + 1, maze, ans);        rat_maze(m, n, r - 1, c, maze, ans);        rat_maze(m, n, r, c - 1, maze, ans);        maze[r][c] = 'O';        ans[r][c] = 0;    }}