public class Replace {    public static void main(String[] args) {        String s = "abchihitfhi";        System.out.println(count(s, 0));        remove(s, "");        replace(s, "");    }    public static int count(String s, int c) {        if (s.length() == 0) {            return c;        }        int x = 0;        String a = "";        if (s.length() > 1) {            a = s.substring(0, 2);        }        if (a.equals("hi")) {            if (s.length() > 2) {                if (s.charAt(2) == 't') {                    x = count(s.substring(2), c);                } else {                    x = count(s.substring(2), c + 1);                }            } else                x = count(s.substring(2), c + 1);        } else {            x = count(s.substring(1), c);        }        return x;    }    public static void remove(String s, String ans) {        if (s.length() == 0) {            System.out.println(ans);            return;        }        String a = "";        if (s.length() > 1)            a = s.substring(0, 2);        if (a.equals("hi")) {            if (s.length() > 2) {                if (s.charAt(2) == 't') {                    remove(s.substring(2), ans + "hi");                } else {                    remove(s.substring(2), ans);                }            } else {                remove(s.substring(2), ans);            }        } else {            remove(s.substring(1), ans + s.charAt(0));        }    }    public static void replace(String s, String ans) {        if (s.length() == 0) {            System.out.println(ans);            return;        }        String a = "";        if (s.length() > 1)            a = s.substring(0, 2);        if (a.equals("hi")) {            if (s.length() > 2) {                if (s.charAt(2) == 't') {                    replace(s.substring(2), ans + "hi");                } else {                    replace(s.substring(2), ans + "bye");                }            } else {                replace(s.substring(2), ans + "bye");            }        } else {            replace(s.substring(1), ans + s.charAt(0));        }    }}