import java.util.ArrayList;import java.util.List;public class Subset {    public static void main(String[] args) {        List<List<Integer>> ans = new ArrayList<>();        ArrayList<Integer> l = new ArrayList<>();        int nums[] = {1, 2, 3};        sub(nums, ans, l, 0);        System.out.println(ans);    }    private static void sub(int[] arr, List<List<Integer>> ans, ArrayList<Integer> l, int i) {        if (arr.length == i) {            ans.add(l);            return;        }        int c = arr[i];        ArrayList<Integer> l1 = new ArrayList<>(l);        l1.add(c);        sub(arr, ans, l1, i + 1);        sub(arr, ans, l, i + 1);    }}