public class Twins {    public static void main(String[] args) {        String s = "AxAxA";        System.out.println(count(s, 0));    }    public static int count(String s, int c) {        if (s.length() == 2) {            return c;        }        int x = 0;        char ch = s.charAt(0);        if (ch == s.charAt(2)) {            x = count(s.substring(1), c + 1);        } else {            x = count(s.substring(1), c);        }        return x;    }}